mapper.select:
SELECT 

FROM t_neighborhood_contact a

WHERE a.id = #{id}

SELECT
FROM t_neighborhood_contact a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.t_neighborhood_main_id = #{neighborhood.id}
AND a.contact_name LIKE '%'||#{contactName}||'%' '%'+#{contactName}+'%' concat('%',#{contactName},'%')
AND a.cell_phone LIKE '%'||#{cellPhone}||'%' '%'+#{cellPhone}+'%' concat('%',#{cellPhone},'%')
AND a.desk_phone LIKE '%'||#{deskPhone}||'%' '%'+#{deskPhone}+'%' concat('%',#{deskPhone},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM t_neighborhood_contact a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_neighborhood_contact SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
