mapper.select:
SELECT 

FROM t_lease_contract_dtl a

WHERE a.id = #{id}

SELECT 

FROM t_lease_contract_dtl a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.lease_contract_id = #{leaseContractId}


ORDER BY a.start_date

SELECT 

FROM t_lease_contract_dtl a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.lease_contract_id = #{leaseContractId}


ORDER BY a.start_date

mapper.update:
UPDATE t_lease_contract_dtl SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
