SELECT 
FROM t_rent_contract a 
WHERE a.id = #{id}

SELECT

FROM t_rent_contract a

WHERE a.contract_code = #{contractCode}

		SELECT 
		FROM t_rent_contract a 

		LEFT JOIN t_contract_tenant tct ON tct.`CONTRACT_ID` = a.`ID` AND tct.`DEL_FLAG` = '0'
  		LEFT JOIN t_tenant tt ON tt.`ID` = tct.`TENANT_ID` AND tt.`DEL_FLAG` = '0' 

			a.del_flag = #{DEL_FLAG_NORMAL}

				AND a.contract_code LIKE
				'%'||#{contractCode}||'%'
				'%'+#{contractCode}+'%'
				concat('%',#{contractCode},'%')


				AND a.contract_name LIKE
				'%'||#{contractName}||'%'
				'%'+#{contractName}+'%'
				concat('%',#{contractName},'%')


				AND a.rent_mode = #{rentMode}


				AND a.property_project_id = #{propertyProject.id}


				AND a.building_id = #{building.id}


				AND a.house_id = #{house.id}


				AND a.room_id = #{room.id}


				AND a.contract_source = #{contractSource}


				AND a.start_date = #{startDate}


				AND a.expired_date = #{expiredDate}


				AND a.sign_date = #{signDate}


				AND a.sign_type = #{signType}


				AND a.contract_name = #{name}


				AND a.contract_status = #{contractStatus}


				AND a.contract_busi_status = #{contractBusiStatus}


				AND a.contract_id = #{contractId}


				AND LOCATE(#{tenantName} , tt.tenant_name) > 0


				AND LOCATE(#{tenantMobileNo} , tt.cell_phone) > 0


				AND LOCATE(#{tenantIdNo} , tt.id_no) > 0

			${sqlMap.dsf}

		union
		SELECT 
		FROM t_rent_contract a 

  		LEFT JOIN t_contract_tenant tct ON tct.`LEASE_CONTRACT_ID` = a.`ID` AND tct.`DEL_FLAG` = '0'
  		LEFT JOIN t_tenant tt ON tt.`ID` = tct.`TENANT_ID` AND tt.`DEL_FLAG` = '0'

			a.del_flag = #{DEL_FLAG_NORMAL}

				AND a.contract_code LIKE
				'%'||#{contractCode}||'%'
				'%'+#{contractCode}+'%'
				concat('%',#{contractCode},'%')


				AND a.contract_name LIKE
				'%'||#{contractName}||'%'
				'%'+#{contractName}+'%'
				concat('%',#{contractName},'%')


				AND a.rent_mode = #{rentMode}


				AND a.property_project_id = #{propertyProject.id}


				AND a.building_id = #{building.id}


				AND a.house_id = #{house.id}


				AND a.room_id = #{room.id}


				AND a.contract_source = #{contractSource}


				AND a.start_date = #{startDate}


				AND a.expired_date = #{expiredDate}


				AND a.sign_date = #{signDate}


				AND a.sign_type = #{signType}


				AND a.contract_name = #{name}


				AND a.contract_status = #{contractStatus}


				AND a.contract_busi_status = #{contractBusiStatus}


				AND a.contract_id = #{contractId}


				AND LOCATE(#{tenantName} , tt.tenant_name) > 0


				AND LOCATE(#{tenantMobileNo} , tt.cell_phone) > 0


				AND LOCATE(#{tenantIdNo} , tt.id_no) > 0

			${sqlMap.dsf}



				ORDER BY ${page.orderBy}


				ORDER BY updateDate DESC

