SELECT COUNT(t.roomid) FROM (SELECT trc.`ROOM_ID` AS roomid FROM t_rent_contract trc WHERE trc.RENT_MODE = '1' AND trc.DEL_FLAG = '0' AND ( (trc.CONTRACT_STATUS = '1' AND (trc.`CONTRACT_BUSI_STATUS` IS NULL OR trc.`CONTRACT_BUSI_STATUS`='')) OR (trc.CONTRACT_STATUS = '2' AND (trc.`CONTRACT_BUSI_STATUS` IS NULL OR trc.`CONTRACT_BUSI_STATUS`='')) OR (trc.CONTRACT_STATUS = '4' AND (trc.`CONTRACT_BUSI_STATUS` IS NULL OR trc.`CONTRACT_BUSI_STATUS`='')) OR (trc.CONTRACT_STATUS = '6' AND trc.`CONTRACT_BUSI_STATUS` in ('0', '1', '2', '3')) ) AND trc.PROPERTY_PROJECT_ID = #{propertyProjectId}
AND trc.START_DATE <= #{startDate} AND trc.expired_date >= #{startDate}
UNION SELECT tr.id AS roomid FROM t_room tr WHERE tr.`HOUSE_ID` IN (SELECT trc.`HOUSE_ID` FROM t_rent_contract trc WHERE trc.RENT_MODE = '0' AND trc.DEL_FLAG = '0' AND ( (trc.CONTRACT_STATUS = '1' AND (trc.`CONTRACT_BUSI_STATUS` IS NULL OR trc.`CONTRACT_BUSI_STATUS`='')) OR (trc.CONTRACT_STATUS = '2' AND (trc.`CONTRACT_BUSI_STATUS` IS NULL OR trc.`CONTRACT_BUSI_STATUS`='')) OR (trc.CONTRACT_STATUS = '4' AND (trc.`CONTRACT_BUSI_STATUS` IS NULL OR trc.`CONTRACT_BUSI_STATUS`='')) OR (trc.CONTRACT_STATUS = '6' AND trc.`CONTRACT_BUSI_STATUS` in ('0', '1', '2', '3')) ) AND trc.PROPERTY_PROJECT_ID = #{propertyProjectId}
AND trc.START_DATE <= #{startDate} AND trc.expired_date >= #{startDate}
) AND tr.`DEL_FLAG` = '0') t

SELECT  * 
FROM 	t_rent_contract trc
WHERE 	trc.RENT_MODE = '1' 
AND 	trc.DEL_FLAG = '0'
AND 	trc.CONTRACT_STATUS = '6'
AND  	trc.CONTRACT_BUSI_STATUS = '0'


		AND trc.START_DATE >=  #{startDate}




		AND trc.START_DATE <=  #{endDate}


AND trc.PROPERTY_PROJECT_ID = #{propertyProjectId}

SELECT COUNT(t.house_id) FROM (SELECT trc.`HOUSE_ID` FROM t_rent_contract trc WHERE trc.RENT_MODE = '0' AND trc.DEL_FLAG = '0' AND ( ( trc.CONTRACT_STATUS = '1' AND ( trc.`CONTRACT_BUSI_STATUS` IS NULL OR trc.`CONTRACT_BUSI_STATUS` = '' ) ) OR ( trc.CONTRACT_STATUS = '2' AND ( trc.`CONTRACT_BUSI_STATUS` IS NULL OR trc.`CONTRACT_BUSI_STATUS` = '' ) ) OR ( trc.CONTRACT_STATUS = '4' AND ( trc.`CONTRACT_BUSI_STATUS` IS NULL OR trc.`CONTRACT_BUSI_STATUS` = '' ) ) OR ( trc.CONTRACT_STATUS = '6' AND trc.`CONTRACT_BUSI_STATUS` in ('0', '1', '2', '3') ) ) AND trc.PROPERTY_PROJECT_ID = #{propertyProjectId}
AND trc.START_DATE <= #{startDate} AND trc.expired_date >= #{startDate}
UNION SELECT DISTINCT trc.`HOUSE_ID` FROM t_rent_contract trc WHERE trc.RENT_MODE = '1' AND trc.DEL_FLAG = '0' AND ( ( trc.CONTRACT_STATUS = '1' AND ( trc.`CONTRACT_BUSI_STATUS` IS NULL OR trc.`CONTRACT_BUSI_STATUS` = '' ) ) OR ( trc.CONTRACT_STATUS = '2' AND ( trc.`CONTRACT_BUSI_STATUS` IS NULL OR trc.`CONTRACT_BUSI_STATUS` = '' ) ) OR ( trc.CONTRACT_STATUS = '4' AND ( trc.`CONTRACT_BUSI_STATUS` IS NULL OR trc.`CONTRACT_BUSI_STATUS` = '' ) ) OR ( trc.CONTRACT_STATUS = '6' AND trc.`CONTRACT_BUSI_STATUS` in ('0', '1', '2', '3') ) ) AND trc.PROPERTY_PROJECT_ID = #{propertyProjectId}
AND trc.START_DATE <= #{startDate} AND trc.expired_date >= #{startDate}
) t

SELECT trc.*
FROM t_rent_contract trc,t_house th
WHERE trc.RENT_MODE = '0'
AND trc.DEL_FLAG = '0'
AND trc.CONTRACT_STATUS = '6'
AND trc.CONTRACT_BUSI_STATUS = '0'
AND trc.PROPERTY_PROJECT_ID = #{propertyProjectId}


		AND trc.START_DATE >=  #{startDate}




		AND trc.START_DATE <=  #{endDate}


AND th.`DEL_FLAG` = '0'
AND th.`HOUSE_STATUS` = '4'
AND th.`DECORA_STRUC_ROOM_NUM` = #{roomNum}
AND th.`DECORA_STRUC_CUSSPAC_NUM` = #{cusspacNum}
AND trc.house_id = th.id

