mapper.select:
SELECT 

FROM t_postpaid_fee a

WHERE a.id = #{id}

SELECT

FROM t_postpaid_fee a

WHERE a.PAYMENT_TRANS_ID = #{paymentTransId}

SELECT

from t_postpaid_fee a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.rent_contract_id = #{rentContractId}


		AND a.payment_trans_id = #{paymentTransId}


		AND a.public_Fee_Type = #{publicFeeType}


		AND a.pay_date >= str_to_date(#{startDate},'%Y-%m-%d')


		AND a.pay_date <= str_to_date(#{endDate},'%Y-%m-%d')


		AND a.pay_status = #{payStatus}


		AND a.settle_status = #{settleStatus}


		AND tc.contract_name like concat('%',#{contractName},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM t_postpaid_fee a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_postpaid_fee
SET del_flag = #{DEL_FLAG_DELETE},
update_by = #{updateBy.id},
update_date = #{updateDate}
WHERE id = #{id}

	AND rent_contract_id = #{rentContractId}
