mapper.select:
SELECT

FROM t_house a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.property_project_id = #{propertyProject.id}


		AND a.building_id = #{building.id}


		AND a.house_no LIKE
		'%'||#{houseNo}||'%'
		'%'+#{houseNo}+'%'
		concat('%',#{houseNo},'%')


		AND a.certificate_no LIKE
		'%'||#{certificateNo}||'%'
		'%'+#{certificateNo}+'%'
		concat('%',#{certificateNo},'%')


		AND a.house_code LIKE
		'%'||#{houseCode}||'%'
		'%'+#{houseCode}+'%'
		concat('%',#{houseCode},'%')


		AND a.house_status = #{houseStatus}


		AND a.ele_account_num = #{eleAccountNum}


		AND a.water_account_num = #{waterAccountNum}


		AND a.gas_account_num = #{gasAccountNum}


		and a.house_status not in('0','2','4')


		and a.intent_mode = #{intentMode}


		and a.is_feature = #{isFeature}


		and a.type = #{type}


		and a.rent_month_gap = #{rentMonthGap}


		and a.depos_month_count = #{deposMonthCount}


		and a.reservation_phone = #{reservationPhone}




${sqlMap.dsf}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM t_house a


	a.del_flag = #{DEL_FLAG_NORMAL}


${sqlMap.dsf}


		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT count(*)
from t_house
WHERE del_flag = #{DEL_FLAG_NORMAL}

mapper.update:
UPDATE t_house SET property_project_id = #{propertyProject.id}, building_id = #{building.id}, owner_id = #{owner.id}, house_code = #{houseCode}, house_no = #{houseNo}, certificate_no = #{certificateNo}, house_floor = #{houseFloor}, house_space = #{houseSpace}, decoration_spance = #{decorationSpance}, ORI_STRUC_ROOM_NUM = #{oriStrucRoomNum}, ORI_STRUC_CUSSPAC_NUM= #{oriStrucCusspacNum}, ORI_STRUC_WASHRO_NUM= #{oriStrucWashroNum}, DECORA_STRUC_ROOM_NUM= #{decoraStrucRoomNum}, DECORA_STRUC_CUSSPAC_NUM= #{decoraStrucCusspacNum}, DECORA_STRUC_WASHRO_NUM= #{decoraStrucWashroNum}, ele_account_num= #{eleAccountNum}, water_account_num= #{waterAccountNum}, gas_account_num= #{gasAccountNum},
house_status = #{houseStatus},
update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks}, intent_mode = #{intentMode}, is_feature = #{isFeature}, rental = #{rental}, short_desc = #{shortDesc}, short_location = #{shortLocation}, service_user = #{serviceUser.id}, type = #{type}, share_area_config = #{shareAreaConfig}, rent_month_gap = #{rentMonthGap}, depos_month_count = #{deposMonthCount}, alipay_status = #{alipayStatus}, up = #{up}, reservation_phone = #{reservationPhone} WHERE id = #{id}

UPDATE t_house SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE  t_house
SET 	house_status = '4',
	    update_by = #{updateBy.id},
	    update_date = #{updateDate}
WHERE   id = #{id}
and     house_status = '1'
and 	del_flag = #{DEL_FLAG_NORMAL}

UPDATE  t_house
SET 	house_status = '4',
	    update_by = #{updateBy.id},
	    update_date = #{updateDate}
WHERE   id = #{id}
and     house_status = '4'
and 	del_flag = #{DEL_FLAG_NORMAL}

UPDATE  t_house
SET 	house_status = '4',
	    update_by = #{updateBy.id},
	    update_date = #{updateDate}
WHERE   id = #{id}
and     house_status = '2'
and 	del_flag = #{DEL_FLAG_NORMAL}

UPDATE  t_house
SET 	house_status = '2',
	    update_by = #{updateBy.id},
	    update_date = #{updateDate}
WHERE   id = #{id}
and     house_status = '1'
and 	del_flag = #{DEL_FLAG_NORMAL}

UPDATE  t_house
SET 	alipay_status = '1',
update_by = #{updateBy.id},
update_date = #{updateDate}

	up = #{up}

WHERE   id = #{id}
