mapper.select:
SELECT 

FROM t_customer a

WHERE a.id = #{id}

SELECT
FROM t_customer a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.sale_user_id = #{user.id}
AND a.true_name LIKE '%'||#{trueName}||'%' '%'+#{trueName}+'%' concat('%',#{trueName},'%')
AND a.gender = #{gender}
AND a.cell_phone LIKE '%'||#{cellPhone}||'%' '%'+#{cellPhone}+'%' concat('%',#{cellPhone},'%')
AND a.ID_NO LIKE '%'||#{idNo}||'%' '%'+#{idNo}+'%' concat('%',#{idNo},'%')
AND a.is_tenant = #{isTenant}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM t_customer a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_customer SET sale_user_id = #{user.id}, true_name = #{trueName}, gender = #{gender}, cell_phone = #{cellPhone},
is_tenant = #{isTenant},
update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks} WHERE id = #{id}

UPDATE t_customer SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
