mapper.select:
select tpp.project_name,tb.building_name,th.house_no,tro.room_no, main.receipt_date,main.receipt_amount,main.ele_recharge_amount, main.payment_type,main.trade_type,main.trade_direction, main.trade_status,main.id,main.trade_id,main.rental from ( select tr.receipt_date,tr.receipt_amount,tr.payment_type,trc.rental, tta.trade_type,tta.trade_direction,tta.trade_status,tta.id, case when trc.ele_recharge_amount is null then 0 else trc.ele_recharge_amount end ele_recharge_amount, case when tda.id is null then trc.id else tda.id end trade_id, case when tda.property_project_id is null then trc.property_project_id else tda.property_project_id end property_project_id, case when tda.building_id is null then trc.building_id else tda.building_id end building_id, case when tda.house_id is null then trc.house_id else tda.house_id end house_id, case when tda.room_id is null then trc.room_id else tda.room_id end room_id from t_receipt tr left join t_trading_accounts tta on tr.trading_accounts_id = tta.id and tta.del_flag=0 left join t_deposit_agreement tda on tta.trade_id = tda.id and tda.del_flag=0 left join t_rent_contract trc on tta.trade_id = trc.id and trc.del_flag=0 where tr.del_flag=0 and tta.trade_status= 1 ) main left join t_building tb on main.building_id = tb.id and tb.del_flag =0 left join t_property_project tpp on main.property_project_id = tpp.id and tpp.del_flag =0 left join t_house th on main.house_id = th.id and th.del_flag =0 left join t_room tro on main.room_id = tro.id and tro.del_flag =0 where 1=1
${whereSqlString}
${orderBySqlString}
