mapper.select:
SELECT 

FROM t_audit_his a


	a.del_flag = #{DEL_FLAG_NORMAL} 
	and a.object_id=#{objectId} 



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_audit_his a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_audit_his SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
