mapper.select:
SELECT 

FROM t_devices a

WHERE a.id = #{id}

SELECT
FROM t_devices a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.device_id LIKE '%'||#{deviceId}||'%' '%'+#{deviceId}+'%' concat('%',#{deviceId},'%')
AND a.device_name LIKE '%'||#{deviceName}||'%' '%'+#{deviceName}+'%' concat('%',#{deviceName},'%')
AND a.device_model LIKE '%'||#{deviceModel}||'%' '%'+#{deviceModel}+'%' concat('%',#{deviceModel},'%')
AND a.device_type = #{deviceType}
AND a.device_brand LIKE '%'||#{deviceBrand}||'%' '%'+#{deviceBrand}+'%' concat('%',#{deviceBrand},'%')
AND a.device_status = #{deviceStatus}
AND a.devices_choose_flag = #{devicesChooseFlag}
AND a.distr_serl_num LIKE '%'||#{distrSerlNum}||'%' '%'+#{distrSerlNum}+'%' concat('%',#{distrSerlNum},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM t_devices a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 
		a.id AS "id",
		a.device_id AS "deviceId",
		a.device_name AS "deviceName",
		a.device_model AS "deviceModel",
		a.device_type AS "deviceType",
		a.device_price AS "devicePrice",
		a.device_brand AS "deviceBrand",
		a.device_status AS "deviceStatus",
		a.devices_choose_flag AS "devicesChooseFlag",
		a.distr_serl_num AS "distrSerlNum",
		a.del_flag AS "delFlag"
FROM t_devices a

	a.del_flag = #{DEL_FLAG_NORMAL}
	and a.device_status != '5'
	and a.device_id =  #{deviceId}
	and a.device_name = #{deviceName}
	and a.device_model = #{deviceModel}
	and a.device_type = #{deviceType}
	and a. device_brand = #{deviceBrand}

mapper.update:
UPDATE t_devices SET 	
	device_status = #{deviceStatus},
	distr_serl_num = #{distrSerlNum},
	update_by = #{updateBy.id},
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE t_devices SET
	devices_choose_flag = #{devicesChooseFlag},
	update_by = #{updateBy.id},
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE t_devices SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
