mapper.select:
SELECT 

FROM t_customer a

WHERE a.id = #{id}

SELECT 

FROM t_customer a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{user.id}


		AND a.contact_name LIKE 
			'%'||#{contactName}||'%'
			'%'+#{contactName}+'%'
			concat('%',#{contactName},'%')


		AND a.gender = #{gender}


		AND a.cell_phone LIKE 
			'%'||#{cellPhone}||'%'
			'%'+#{cellPhone}+'%'
			concat('%',#{cellPhone},'%')


		AND a.is_tenant = #{isTenant}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_customer a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_customer SET user_id = #{user.id}, contact_name = #{contactName}, gender = #{gender}, cell_phone = #{cellPhone},
is_tenant = #{isTenant},
update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks} WHERE id = #{id}

UPDATE t_customer SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
