mapper.select:
SELECT 
	
FROM t_app_user a

WHERE a.id = #{id}

SELECT
FROM t_app_user a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 
	
FROM t_app_user a


	a.del_flag = #{DEL_FLAG_NORMAL}
		

	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_app_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
