mapper.select:
SELECT 
	
FROM t_contract_book a

WHERE a.id = #{id} and a.del_flag='0' AND a.user_id = #{userId}

SELECT 
	
FROM t_contract_book a

WHERE (a.house_id = #{houseId} or a.room_id = #{roomId}) and book_status in('0','1') and a.del_flag='0' AND a.user_id = #{userId}

SELECT da.id AS "depositId", da.CREATE_DATE, da.del_flag, da.update_by updateUser, tt.pay_way, tt.house_id, CONCAT(tt.house_code, tt.room_no) "roomNo", da.agreement_status AS "bookStatus", tt.short_desc "shortDesc", tt.short_location, tt.attachment_path "attachmentPath", ct.cell_phone FROM T_DEPOSIT_AGREEMENT da LEFT JOIN (SELECT DEPOSIT_AGREEMENT_ID, te.cell_phone, te.id_no FROM t_contract_tenant cte, t_tenant te WHERE cte.tenant_id = te.id AND cte.del_flag = '0' AND te.del_flag = '0') ct ON da.id = ct.deposit_agreement_id LEFT JOIN (SELECT p.project_name, b.building_name, h.pay_way, h.id house_id, h.house_code, '' room_no, h.short_desc, h.short_location, a.attachment_path FROM t_house h LEFT JOIN t_attachment a ON h.`ID` = a.`HOUSE_ID`, t_property_project p, t_building b WHERE h.property_project_id = p.id AND h.building_id = b.id UNION ALL SELECT p.project_name, b.building_name, r.pay_way, r.id house_id, h.house_code, CONCAT('-', r.room_no) room_no, r.short_desc, r.short_location, a.attachment_path FROM t_room r LEFT JOIN t_attachment a ON r.id = a.`ROOM_ID`, t_property_project p, t_building b, t_house h WHERE r.property_project_id = p.id AND r.building_id = b.id AND r.house_id = h.id) tt ON ( ( da.house_id = tt.house_id AND da.rent_mode = '0' ) OR da.room_id = tt.house_id ) WHERE ct.cell_phone = #{userPhone} and da.data_source='2'
AND da.id = #{depositId}
ORDER BY create_date DESC

SELECT da.id AS "contractId", da.CONTRACT_CODE AS "contractCode", da.RENTAL AS "rent", tt.house_id, da.contract_status AS "bookStatus", da.contract_busi_status AS "contractBusiStatus", tt.short_desc, tt.short_location, tt.attachment_path, ct.cell_phone, da.del_flag, da.update_by updateUser, da.expired_date end_date FROM T_RENT_CONTRACT da LEFT JOIN (SELECT LEASE_CONTRACT_ID, te.cell_phone FROM t_contract_tenant cte, t_tenant te WHERE cte.tenant_id = te.id AND cte.del_flag = '0' AND te.del_flag = '0') ct ON da.id = ct.LEASE_CONTRACT_ID LEFT JOIN (SELECT p.project_name, b.building_name, h.id house_id, h.house_code, '' room_no, h.short_desc, h.short_location, a.attachment_path FROM t_house h LEFT JOIN t_attachment a ON h.`ID` = a.`HOUSE_ID`, t_property_project p, t_building b WHERE h.property_project_id = p.id AND h.building_id = b.id UNION ALL SELECT p.project_name, b.building_name, r.id house_id, h.house_code, r.room_no, r.short_desc, r.short_location, a.attachment_path FROM t_room r LEFT JOIN t_attachment a ON r.id = a.`ROOM_ID`, t_property_project p, t_building b, t_house h WHERE r.property_project_id = p.id AND r.building_id = b.id AND r.house_id = h.id) tt ON ( ( da.house_id = tt.house_id AND da.rent_mode = '0' ) OR da.room_id = tt.house_id ) WHERE ct.cell_phone = #{userPhone} and da.data_source='2'
AND da.id = #{contractId}
AND da.contract_busi_status = #{contractBusiStatus}
order by da.create_date desc

SELECT 
	
FROM t_contract_book a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
		AND a.user_name = #{userName}
	
	
		AND a.user_id = #{userId}
	
	
		AND a.user_phone = #{userPhone}
	
	
		AND date_format(a.book_date,'%Y-%m-%d') = date_format(#{bookDate},'%Y-%m-%d')
	
	
		AND a.book_status = #{bookStatus}
	


	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC

SELECT 
	
FROM t_contract_book a


	a.del_flag = #{DEL_FLAG_NORMAL}
		

	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_contract_book SET 	
	book_status = #{bookStatus}
WHERE id = #{id}

UPDATE t_contract_book SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE house_id = #{houseId} or room_id = #{roomId}
