mapper.select:
SELECT 
	
FROM t_deposit_agreement a

WHERE a.id = #{id}

SELECT 
	
FROM t_deposit_agreement a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
		AND a.property_project_id = #{propertyProject.id}
	
	
		AND a.building_id = #{building.id}
	
	
		AND a.house_id = #{house.id}
	
	
		AND a.room_id = #{room.id}
	
	
		AND a.rent_mode = #{rentMode}
	
	
		AND a.agreement_code LIKE 
			'%'||#{agreementCode}||'%'
			'%'+#{agreementCode}+'%'
			concat('%',#{agreementCode},'%')
	
	
		AND a.agreement_name LIKE 
			'%'||#{agreementName}||'%'
			'%'+#{agreementName}+'%'
			concat('%',#{agreementName},'%')
	
	
		AND a.agreement_status = #{agreementStatus}
	
	
		AND a.agreement_busi_status = #{agreementBusiStatus}
	


	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC

SELECT 
	
FROM t_deposit_agreement a


	a.del_flag = #{DEL_FLAG_NORMAL}
		

	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_deposit_agreement SET property_project_id = #{propertyProject.id}, building_id = #{building.id}, house_id = #{house.id}, room_id = #{room.id}, rent_mode = #{rentMode}, user_id = #{user.id}, agreement_code = #{agreementCode}, agreement_name = #{agreementName}, start_date = #{startDate}, expired_date = #{expiredDate}, sign_date = #{signDate}, ren_months = #{renMonths}, deposit_months = #{depositMonths}, agreement_date = #{agreementDate}, deposit_amount = #{depositAmount}, housing_rent = #{housingRent}, agreement_status = #{agreementStatus}, agreement_busi_status = #{agreementBusiStatus}, update_by = #{updateBy.id},
update_by = #{updateUser},
update_date = #{updateDate}, remarks = #{remarks} WHERE id = #{id}

UPDATE t_deposit_agreement SET 
	del_flag = #{DEL_FLAG_DELETE}
	
		,update_by = #{updateUser}
	
WHERE id = #{id}
