mapper.select:
SELECT 

FROM t_room a

WHERE a.id = #{id}

SELECT 

FROM t_room a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.property_project_id = #{propertyProject.id}


		AND a.building_id = #{building.id}


		AND a.house_id = #{house.id}


		AND a.room_no LIKE 
			'%'||#{roomNo}||'%'
			'%'+#{roomNo}+'%'
			concat('%',#{roomNo},'%')


		AND a.room_status = #{roomStatus}


		and a.room_status not in('0','2','3','5') 


		and a.is_feature = #{isFeature}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT
		a.id AS "id",
		a.property_project_id AS "propertyProject.id",
		a.building_id AS "building.id",
		a.house_id AS "house.id",
		a.room_no AS "roomNo",
		a.room_status AS "roomStatus",
		a.del_flag AS "delFlag",
		a.is_feature as "isFeature"
FROM t_room a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
	  AND a.property_project_id = #{propertyProject.id}
	  AND a.building_id = #{building.id}
	  AND a.house_id = #{house.id}
	  AND a.room_no = #{roomNo}

SELECT 

FROM t_room a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_room SET property_project_id = #{propertyProject.id}, building_id = #{building.id}, house_id = #{house.id}, room_no = #{roomNo}, meter_no = #{meterNo}, room_space = #{roomSpace}, orientation = #{orientation}, structure = #{structure},
room_status = #{roomStatus},
update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks}, is_feature = #{isFeature} WHERE id = #{id}

UPDATE t_room SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
