mapper.select:
SELECT 

FROM t_message a

WHERE a.id = #{id}

SELECT 

FROM t_message a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.content = #{content}


		AND a.receiver = #{receiver}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_message a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_message SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
