mapper.select:
SELECT 
	
FROM t_company_linkman a

WHERE a.id = #{id}

SELECT
FROM t_company_linkman a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.company_id = #{company.id}
AND a.person_name LIKE '%'||#{personName}||'%' '%'+#{personName}+'%' concat('%',#{personName},'%')
AND a.cell_phone LIKE '%'||#{cellPhone}||'%' '%'+#{cellPhone}+'%' concat('%',#{cellPhone},'%')
AND a.tell_phone LIKE '%'||#{tellPhone}||'%' '%'+#{tellPhone}+'%' concat('%',#{tellPhone},'%')
AND a.email LIKE '%'||#{email}||'%' '%'+#{email}+'%' concat('%',#{email},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 
	
FROM t_company_linkman a


	a.del_flag = #{DEL_FLAG_NORMAL}
		

	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_company_linkman SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
