mapper.select:
SELECT 

FROM t_owner a

WHERE a.id = #{id}

SELECT 

FROM t_owner a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.social_number = #{socialNumber}


		AND a.cell_phone = #{cellPhone}


		AND a.desk_phone = #{deskPhone}


		AND a.address LIKE 
			'%'||#{address}||'%'
			'%'+#{address}+'%'
			concat('%',#{address},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_owner a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_owner SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
