mapper.select:
SELECT 
	
FROM t_tenant a

WHERE a.id = #{id}

SELECT
FROM t_tenant a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.user_id = #{user.id}
AND a.company_id = #{company.id}
AND a.tenant_name LIKE '%'||#{tenantName}||'%' '%'+#{tenantName}+'%' concat('%',#{tenantName},'%')
AND a.gender = #{gender}
AND a.tenant_type = #{tenantType}
AND a.id_type = #{idType}
AND a.id_no LIKE '%'||#{idNo}||'%' '%'+#{idNo}+'%' concat('%',#{idNo},'%')
AND a.birthday = #{birthday}
AND a.degrees = #{degrees}
AND a.cell_phone LIKE '%'||#{cellPhone}||'%' '%'+#{cellPhone}+'%' concat('%',#{cellPhone},'%')
AND a.email LIKE '%'||#{email}||'%' '%'+#{email}+'%' concat('%',#{email},'%')
AND a.house_register LIKE '%'||#{houseRegister}||'%' '%'+#{houseRegister}+'%' concat('%',#{houseRegister},'%')
AND a.position LIKE '%'||#{position}||'%' '%'+#{position}+'%' concat('%',#{position},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 
	
FROM t_tenant a


	a.del_flag = #{DEL_FLAG_NORMAL}
		

	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_tenant SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
